  UploadFileDoc(e) {
    this.mainDocSpinner = true;
    this.call_modal = false;
    this.file = e.target.files[0];
    this.fileName = e.target.files[0].name;
    const record = this.documentsArr.find(item => {
      return item.DOC_NAME == this.fileName;
    });

    if (record) {
      setTimeout(() => {
        this.call_modal = true;
        this.data_send = {
          text: 'This File Already Exists',
          active: this.call_modal
        };
      }, 0);
      this.mainDocSpinner = false;
    } else {
      if (this.initValue + parseFloat(this.file.size) / 1024 / 1024 > 50) {
        setTimeout(() => {
          this.call_modal = true;
          this.data_send = {
            text: 'Document Size Limit Exceeded,You have total limit upto 50MB !',
            active: this.call_modal
          };
        }, 0);
        this.mainDocSpinner = false;
        return;
      } else {
        this.service.uploadFile("UploadFDHLDocumnets", this.file, [], this.namespace, false, false).then(resDocPath => {
          this.mainDocSpinner = false;
          this.initValue = this.initValue + parseFloat(this.file.size) / 1024 / 1024;
          this.documentsArr.push({
            'DOC_REF_NO': '',
            'DOC_NAME': this.service.datavalidate(this.fileName),
            "DOC_PATH": resDocPath,
            'DOC_TYPE': 'Main',
            'UPLOADERNAME': this.loginUserID,
            'UPLOADED_ON': this.datepipe.transform(new Date(), 'dd-MM-yyyy'),
            'DOC_SIZE': e.target.files[0].size / 1024 / 1024
          });
          this.fileUploadDoc = '';
        }).catch(err => {
          this.mainDocSpinner = false;
        });
      }
    }
  }

