  completeTask(decision, openas, remarks) {
    this.call_modal = false;

    if (this.isRemarksValid(decision)) {
      setTimeout(() => {
        this.call_modal = true;
        this.data_send = {
          text: 'Please enter remarks',
          active: this.call_modal
        };
      }, 0);
      return;
    }

    let data = {
      "FD_HL_OPSchemaFragment": {
        ApproverDecision: decision,
        ApprovedBy: this.loginUserID,
        Remarks: this.remarks
      }
    };
    this.call_modal = false;
    var titlest = decision == 1 ? "Task Completed Successfully" : decision == 0 ? "Task Seek Clarification Successfully" : decision == 7 ? "Task Submitted Successfully" : decision == 4 ? "Task Obsoleted Successfully" : decision == 3 ? "Task Rejected Successfully" : "";

    let _this = this;

    $.cordys.workflow.completeTask(this.taskId, data, {
      dataType: 'xml'
    }).done(() => {
      setTimeout(() => {
        this.call_modal = true;
        this.data_send = {
          text: titlest,
          active: this.call_modal,
          from: 'task'
        };
      }, 0); // _this.closeTask(openas);
    });
  }

