                this.service.taskPage.next(true);
                this.service.spinner.next(true);
                this.getFDHLRequestDetails(this.HR_REF_NO);
                this.getFD_HLDocDetailsByHR_RefNo(this.HR_REF_NO);
                this.getFD_HLHistoryDetailsByHR_RefNo();
            }
        });
        this.getopenAs();
    }
