    isRemarksValid(arg) {
        if (arg == 0 && this.remarks == '' || arg == 3 && this.remarks == '' || arg == 4 && this.remarks == '' || arg == 7 && this.remarks == '') {
            this.remarkSubmit = true;
            return true;
        }
        else {
